import { Component, EventEmitter, Output, ViewChild, ElementRef } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'app';

  clickEventTest(fullname: string, username: string, email: string, password: string){
    alert(`Fullname: ${fullname} Username: ${username} Email: ${email} Password: ${password}`)
  }
}
